      const leaveRoomSport = () => {
            let room = '123'
            let name = 'demo 2'
            socket.emit('leaveGroup', {name, room});
      }








      // -------------------------


      const online = () => {
            let user_id = 711
            socket.emit('online', {user_id});
      }

      const offline = () => {
            let user_id = 711
            socket.emit('offline', {user_id});
      }

